import React, { useEffect, useRef, useState } from 'react'
import styled from 'styled-components'
import { setAuthToken } from '../../../../../../api'
import { getPastor } from '../../../../../../api/admin-pastor'
import InfiniteScroll from 'react-infinite-scroll-component'
import { PastorBox } from '../../pastorBox'
import {
  InnerBoxHeader,
  InnerBoxTitle,
  Margin24,
  Margin36,
} from '../../../../../../component/styledComponent'
import { InfoBox } from '../../../../../../component/box/info'
import { PastorBoxOrder } from '../../pastorBoxOrder'

const ModalContentContainer = styled.div`
  // max-height: 80vh;
  overflow-y: auto;
`

const ModalOverlay = styled.div`
  z-index: 1;
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
`

const ModalWrapper = styled.div`
  position: relative;
  width: 714px;
  background-color: #ffffff;
  border-radius: 20px;
`

const ModalCloseButton = styled.button`
  top: 10px;
  right: 20px;
  width: 30px;
  height: 30px;
  border-radius: 50%;
  background-color: #ffffff;
  border: none;
  cursor: pointer;
`

const PastorContainer = styled.div`
  max-height: 450px;
  // overflow: scroll;
`
const NoticeModalContent = styled.div`
  // display: flex;
  width: 100%;
  // padding: 0 36px 0 36px;
  // flex-direction: column;
  justify-content: center;
  align-items: center;
`

const MainTitleText = styled.div`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 28px;
  line-height: 36px;
  color: #000000;
`

const DataContainer = styled.div`
  padding: 0 36px 0 36px;
  gap: 48px;
  justify-content: space-between;
`
interface PastorData {
  church_pastor_id: string
  order: string
  name: string
  role: string
  image: string
}
interface NoticeModalProps {
  onCloseModal: () => void
}

const pagePerLimit = 6
export const PatchOrderModal = (props: NoticeModalProps) => {
  const { onCloseModal } = props
  const [offset, setOffset] = useState<number>(0)
  const [maxCount, setMaxCount] = useState<number>(0)
  const [hasMore, setHasMore] = useState<boolean>(true)
  const [pastorData, setPastorData] = useState<Array<PastorData>>([])
  const currentDragIndex = useRef<number>(-1)

  const dragFunction = (event: any, type: string) => {
    if (type === 'drop') {
      console.log(event.target)
    }
    event.preventDefault()
    event.stopPropagation()
  }

  const onDragOver = (event: any, index: number) => {
    // console.log(index, 'Over')
  }
  const onDrop = (event: any, index: number) => {
    if (index === -1) {
      currentDragIndex.current = -1
    } else {
      if (currentDragIndex.current !== index) {
        pastorData.splice(currentDragIndex.current, 1)
      }
      console.log(index, 'Drop =')
      console.log('currentDragIndex', currentDragIndex.current)
      currentDragIndex.current = -1
    }
  }
  const onDragEnter = (event: any, index: number) => {
    if (currentDragIndex.current === -1) currentDragIndex.current = index
  }
  const onDragLeave = (event: any, index: number) => {
    // console.log(index, 'Leave')
  }

  const handleCloseModal = () => {
    onCloseModal()
  }

  const handleWrapperClick = (
    event: React.MouseEvent<HTMLDivElement, MouseEvent>
  ) => {
    event.stopPropagation()
  }

  useEffect(() => {
    const token = localStorage.getItem('token')
    if (token) {
      setAuthToken(token)
    }

    const fetchData = async () => {
      const response = await getPastor(offset)
      setPastorData(response.payload.church_pastor_list.results)
      setMaxCount(response.payload.church_pastor_list.total_count)
      setHasMore(
        response.payload.church_pastor_list.total_count > pagePerLimit
          ? true
          : false
      )
    }
    fetchData()
  }, [])

  const loadMoreItem = async () => {
    if (offset + pagePerLimit < maxCount) {
      const response = await getPastor(offset + pagePerLimit)
      setPastorData([
        ...pastorData,
        ...response.payload.church_pastor_list.results,
      ])
      setOffset(offset + pagePerLimit)
    }
    setHasMore(false)
  }

  useEffect(() => {
    document.body.style.cssText = `
      position: fixed; 
      top: -${window.scrollY}px;
      overflow-y: scroll;
      width: 100%;`
    return () => {
      const scrollY = document.body.style.top
      document.body.style.cssText = ''
      window.scrollTo(0, parseInt(scrollY || '0', 10) * -1)
    }
  }, [])

  return (
    <ModalOverlay onClick={handleCloseModal}>
      <ModalWrapper onClick={handleWrapperClick}>
        <Margin36 />
        <InnerBoxHeader>
          <InnerBoxTitle>섬기는 이 순서변경</InnerBoxTitle>
          <ModalCloseButton onClick={onCloseModal}>
            <img src="source/icon/close.svg" alt="close" />
          </ModalCloseButton>
        </InnerBoxHeader>
        <Margin24 style={{ background: '#ffffff' }} />
        <DataContainer>
          <InfoBox text="순서를 변경하고 싶다면 숫자를 변경하거나, 박스를 잡고 순서를 변경할 수 있어요." />
        </DataContainer>
        <Margin24 style={{ background: '#ffffff' }} />
        <ModalContentContainer>
          {/* <NoticeModalContent> */}
          <PastorContainer
            onDragOver={event => dragFunction(event, 'over')}
            onDrop={event => onDrop(event, -1)}
            onDragEnter={event => dragFunction(event, 'enter')}
            onDragLeave={event => dragFunction(event, 'leave')}
          >
            {pastorData.length !== 0 && (
              <InfiniteScroll
                dataLength={pastorData.length}
                next={loadMoreItem}
                hasMore={hasMore}
                scrollThreshold={1}
                loader={<h3>Loading...</h3>}
              >
                {pastorData.map((pastor, index) => {
                  return (
                    <PastorBoxOrder
                      key={pastor.church_pastor_id}
                      index={index}
                      image={pastor.image || 'source/no_image.svg'}
                      name={pastor.name}
                      role={pastor.role}
                      changeIndex={loadMoreItem}
                      onDragOver={onDragOver}
                      onDrop={onDrop}
                      onDragEnter={onDragEnter}
                      onDragLeave={onDragLeave}
                    />
                  )
                })}
              </InfiniteScroll>
            )}
          </PastorContainer>
          {/* </NoticeModalContent> */}
        </ModalContentContainer>
      </ModalWrapper>
    </ModalOverlay>
  )
}
