import React, { Dispatch, SetStateAction, useEffect, useRef, useState } from "react";
import styled from "styled-components";
import Select from 'react-select'
import { setAuthToken } from "../../../../../api";
import { deleteChurchNotification, patchChurchNotification } from "../../../../../api/admin-notification";
import { deleteChurchWeekly, patchChurchWeekly } from "../../../../../api/admin-weekly";

const ModalOverlay = styled.div`
  z-index: 1;
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
`;

const ModalWrapper = styled.div`
  position: relative;
  width: 700px;
  height: 600px;
  background-color: #ffffff;
  border-radius: 20px;
`;

const ModalCloseButton = styled.button`
  position: absolute;
  top: 10px;
  right: 20px;
  width: 30px;
  height: 30px;
  border-radius: 50%;
  background-color: #FFFFFF;
  border: none;
  cursor: pointer;
`;

const NoticeModalContent = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100%;
`;

const ButtonStyle = `
  width: 100%;
  height: 48px;
  border-radius: 16px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  text-align: center;
  cursor: pointer;
`;

const UploadButton = styled.button`
  ${ButtonStyle}
`;

const DeleteButton = styled.button`
  ${ButtonStyle}
`;

const WeeklyDateSelect = styled.div`
width: 303px;
height: 56px;
`;

const Image = styled.div`
  margin-top: 24px;
  width: 202px;
  height: 202px;
  background: #f0f1f3;
  border-radius: 16px;
  overflow: hidden;
  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`;

const InputBox = styled.input`
  width: 100%;
  box-sizing: border-box;
  margin-top: 16px;
  border-radius: 16px;
  height: 56px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 24px;
  border: 1px solid #c1c2c3;
  padding: 0 78px 0 16px;
  color: ${(props) => (props.value ? "#000000" : "#797a7b")};
  &:focus {
    outline: 2px solid #000000;
    outline-offset: -1px;
  }
`;

const SubTitleText = styled.div`
  text-align: left;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 24px;
  line-height: 32px;
  color: #000000;
`;

const DataBox = styled.div`
  width: 100%;
`;


interface WeeklyModalProps {
  refresh: boolean;
  setRefresh: Dispatch<SetStateAction<boolean>>;
  onCloseModal: () => void;
  church_pastor_id: string;
  pastor_index: string;
  pastor_name: string;
  pastor_role: string;
  pastor_image: string;
}


export const PastorInfoModal = (props: WeeklyModalProps) => {
  const { refresh, setRefresh ,onCloseModal, church_pastor_id, pastor_index, pastor_name, pastor_role, pastor_image } = props;

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      setAuthToken(token);
    }
  }, []);
  const [image, setImage] = useState(weekly_image);
  const [title, setTitle] = useState(weekly_title)
  const [weeklyYear, setWeeklyYear] = useState(weekly_year);
  const [weeklyMonth, setWeeklyMonth] = useState(weekly_month);
  const [uploadImage, setUploadImage] = useState<any>([]);
  const imageFile = useRef<HTMLInputElement>(null)
  const openImageFile = () => {
    if (imageFile.current)
      imageFile.current.click()
  }
  const saveImageFile = (event: any) => {
    if (event.target.files !== null && event.target.files[0] !== undefined) {
      setUploadImage(event.target.files[0])
      setImage(URL.createObjectURL(event.target.files[0]))
    }
  }

  const onClickSaveButton = async() => {
    await patchChurchWeekly(uploadImage, church_pastor_id, title, weeklyYear, weeklyMonth)
    setRefresh(!refresh)
    onCloseModal()
  }
  const onClickDeleteButton = async() => {
    await deleteChurchWeekly(church_pastor_id)
    setRefresh(!refresh)
    onCloseModal()
  }

  return (
      <ModalOverlay >
          <ModalWrapper >
              <ModalCloseButton onClick={onCloseModal}><img src="source/icon/close.svg" alt="close" /></ModalCloseButton>
        <NoticeModalContent>
        <DataBox>
          <InputBox
            type="text"
            value={title}
            placeholder={"제목 입력"}
            onChange={(e) => setTitle(e.target.value)}
          />
          </DataBox>
          <WeeklyDateSelect>
              <Select
                options={yearOptions}
                defaultValue={yearOptions[1]}
                onChange={(e) => e && setWeeklyYear(e?.value)}
                isSearchable={false}
                theme={(theme) => ({ // change color
                  ...theme,
                  borderRadius: 16,
                  colors: {
                    ...theme.colors,
                    primary25: '#F0E6FD',
                    primary: '#5500DD',
                    neutral20: '#000000',
                  },
                })}
              />
            </WeeklyDateSelect>
            <WeeklyDateSelect>
              <Select
                options={monthOptions}
                defaultValue={monthOptions[Number(weeklyMonth)-1]}
                onChange={(e) => e && setWeeklyMonth(e?.value)}
                isSearchable={false}
                theme={(theme) => ({ // change color
                  ...theme,
                  borderRadius: 16,
                  colors: {
                    ...theme.colors,
                    primary25: '#F0E6FD',
                    primary: '#5500DD',
                    neutral20: '#000000',
                  },
                })}
              />
            </WeeklyDateSelect>
        <Image><img src={image || "source/no_image.svg"} alt="교회 로고" /></Image>
          <UploadButton
              style={{
                background: "#ffffff" ,
                border: "1px solid #5500DD",
                color: "#5500DD" ,
              }}
              onClick={openImageFile}
            >
              <input type='file' id='file' ref={imageFile} style={{ display: 'none' }} onChange={saveImageFile} />
              이미지 변경
            </UploadButton>


          <UploadButton
              style={{
                background: "#ffffff" ,
                border: "1px solid #5500DD",
                color: "#5500DD" ,
              }}
              onClick={onClickSaveButton}
            >
              저장
            </UploadButton>
            <DeleteButton
              style={{
                background: "#ffffff" ,
                border: "1px solid #D40030",
                color: "#D40030" ,
              }}
              onClick={onClickDeleteButton}
            >
              삭제
          </DeleteButton>

              </NoticeModalContent>
          </ModalWrapper>
      </ModalOverlay>
  );
};
