import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { BrowserRouter, Router, Route, useNavigate } from "react-router-dom";
import {setAuthToken } from "../../api";

import { useLocation } from "react-router";
import { InformationPage } from "./admin-information";
import { NotificationPage } from "./admin-notification";
import { WeeklyPage } from "./admin-weekly";
import { SchedulePage } from "./admin-schedule";
import { PastorPage } from "./admin-pastor";
import ErrorPage from "../../error-pages";
import { getChurchInfo } from "../../api/admin-information";

const Container = styled.div`
  display: flex;
  width: 100%;
`;

const InnerContainer = styled.div`
  margin-left: 300px;
  height: 100%;
  width: 100%;
`;

const Sideheader = styled.div`
  width: 300px;
  height: 100%;
  background-color: #ffffff;
  position: fixed;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Mainlogo = styled.div`
  margin-top: 48px;
  border-radius: 50%;
  overflow: hidden;
  width: 64px;
  height: 64px;
  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`;

const Maintitle = styled.div`
  margin-top: 16px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 28px;
  line-height: 36px;
  text-align: center;
  color: #000000;
`;

const Logout = styled.button`
  margin-top: 16px;
  width: 168px;
  height: 48px;
  top: 180px;
  background: #ffffff;
  border: 1px solid #5500dd;
  border-radius: 16px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  text-align: center;
  color: #5500dd;
  cursor: pointer;
`;

const SideButtonBox = styled.div`
  margin-top: 72px;
`;

const SideButton = styled.button`
  display: flex;
  margin-top: 16px;
  width: 268px;
  height: 64px;
  padding: 16px 24px 16px 24px;
  background: #5500DD;
  gap: 16px;
  border-radius: 16px;
  cursor: pointer;
  ont-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 24px;
  line-height: 32px;
  border: none;
  color: #FFFFFF;
`;

const ButtonImage = styled.img`
  width: 32px;
  height: 32px;
`;

export const AdminPage: React.FC = () => {
  const token = useLocation().state;
  const navigate = useNavigate();
  const defaultLogo = "/source/main_logo.png";

  if (!token) {
    navigate("/ErrorPage");
  }

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/AdminLogin", { replace: true });
  };

  interface ChurchInfo {
    church_id: string;
    name: string;
    contact_number: string;
    denomination: string;
    denomination_list: string[];
    introduction_title: string;
    introduction_content: string;
    is_exposure: boolean;
    address: string;
    detail_address: string;
    thumbnail: string;
    logo: string;
    directions_parking: string;
    directions_own_car: string;
    directions_public_transport: string;
    directions_shuttle_bus: string;
  }

  const [churchInfo, setChurchInfo] = useState<ChurchInfo | null>(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      setAuthToken(token);
    }

    const fetchData = async () => {
      const response = await getChurchInfo();
      setChurchInfo(response.payload.church_info);
    };

    fetchData();
  }, []);

  const [informationState, setInformationState] = useState(true);
  const [notificationState, setNotificationState] = useState(false);
  const [weeklyState, setWeeklyState] = useState(false);
  const [scheduleState, setScheduleState] = useState(false);
  const [pastorState, setPastorState] = useState(false);


  const handleInformation = () => {
    setInformationState(true);
    setNotificationState(false);
    setWeeklyState(false);
    setScheduleState(false);
    setPastorState(false);
  };

  const handleNotification = () => {
    setInformationState(false);
    setNotificationState(true);
    setWeeklyState(false);
    setScheduleState(false);
    setPastorState(false);
  };
  const handleWeekly = () => {
    setInformationState(false);
    setNotificationState(false);
    setWeeklyState(true);
    setScheduleState(false);
    setPastorState(false);
  };
  const handleSchedule = () => {
    setInformationState(false);
    setNotificationState(false);
    setWeeklyState(false);
    setScheduleState(true);
    setPastorState(false);
  };
  const handlePastor = () => {
    setInformationState(false);
    setNotificationState(false);
    setWeeklyState(false);
    setScheduleState(false);
    setPastorState(true);
  };

  return (
    <Container>
      <Sideheader>
        <Mainlogo>
          <img src={churchInfo?.logo || defaultLogo} alt="church logo" />
        </Mainlogo>
        <Maintitle>{churchInfo?.name || "주다"}</Maintitle>
        <Logout onClick={handleLogout}>로그아웃</Logout>
        <SideButtonBox>
          <SideButton onClick={handleInformation} style={{
            background: informationState ? "#5500DD" : "#ffffff",
            color: informationState ? "#ffffff" : "#000000",
          }}><ButtonImage
              src={
                informationState
                  ? "/source/icon/side_information_active.svg"
                  : "/source/icon/side_information_deactive.svg"
              }
            />정보 수정</SideButton>
          <SideButton onClick={handleNotification} style={{
            background: notificationState ? "#5500DD" : "#ffffff",
            color: notificationState ? "#ffffff" : "#000000",
          }}><ButtonImage
              src={
                notificationState
                  ? "/source/icon/side_notice_active.svg"
                  : "/source/icon/side_notice_deactive.svg"
              }
            />공지사항</SideButton>
          <SideButton onClick={handleWeekly} style={{
            background: weeklyState ? "#5500DD" : "#ffffff",
            color: weeklyState ? "#ffffff" : "#000000",
          }}><ButtonImage
              src={
                weeklyState
                  ? "/source/icon/side_weekly_active.svg"
                  : "/source/icon/side_weekly_deactive.svg"
              }
            />주보</SideButton>
          <SideButton onClick={handleSchedule} style={{
            background: scheduleState ? "#5500DD" : "#ffffff",
            color: scheduleState ? "#ffffff" : "#000000",
          }}><ButtonImage
              src={
                scheduleState
                  ? "/source/icon/side_calendar_active.svg"
                  : "/source/icon/side_calendar_deactive.svg"
              }
            />일정</SideButton>
          <SideButton onClick={handlePastor} style={{
            background: pastorState ? "#5500DD" : "#ffffff",
            color: pastorState ? "#ffffff" : "#000000",
          }}><ButtonImage
              src={
                pastorState
                  ? "/source/icon/side_pastor_active.svg"
                  : "/source/icon/side_pastor_deactive.svg"
              }
            />신도 관리</SideButton>
        </SideButtonBox>
      </Sideheader>
      <InnerContainer>
        {informationState && <InformationPage />}
        {notificationState && <NotificationPage />}
        {weeklyState && <WeeklyPage />}
        {scheduleState && <SchedulePage />}
        {pastorState && <PastorPage />}
      </InnerContainer>
    </Container>
  );
};
