import React, { Dispatch, SetStateAction, useRef } from "react";
import Select from 'react-select'
import styled from "styled-components";
import { useState } from "react";
import { createChurchWeekly } from "../../../../api/admin-weekly";

const Container = styled.div`
  width: 100%;
  background: #f0f1f3;
`;

const Header = styled.div`
  display: flex;
  min-width: 884px;
  justify-content: space-between;
  margin-left: 48px;
  margin-right: 48px;
`;

const MainTitleText = styled.div`
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 28px;
  line-height: 36px;
  color: #000000;
`;

const SubTitleText = styled.div`
  text-align: left;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 24px;
  line-height: 32px;
  color: #000000;
`;

const SubTitleColorText = styled.text`
  color: #5500DD;
`;

const ModifyButtonBox = styled.div`
  display: flex;
  gap: 16px;
`;

const ModifyButton = styled.button`
  width: 168px;
  height: 48px;
  border-radius: 16px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  text-align: center;
  cursor: pointer;
`;

const AddButtonBox = styled.div`
  display: flex;
  justify-content: center;
  gap: 16px;
`;

const AddButton = styled.button`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 72px;
  border-radius: 16px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 24px;
  color: #5500DD;
  text-align: center;
  cursor: pointer;
`;

const buttonStyle = `
  width: 100%;
  height: 48px;
  border-radius: 16px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  text-align: center;
  cursor: pointer;
`;

const UploadButton = styled.button`
  ${buttonStyle}
`;

const DeleteButton = styled.button`
  ${buttonStyle}
`;

const DataBoxContainer = styled.div`
  margin-top: 24px;
  margin-left: 48px;
  width: calc(100% - 96px);
  min-width: 884px;
  background: #ffffff;
  box-shadow: 0px 0px 24px rgba(0, 0, 0, 0.16);
  border-radius: 24px;
  flex: none;
  order: 1;
  flex-grow: 0;
`;

const DataContainer = styled.div`
  padding: 48px 0 0 48px;
  width: calc(100% - 96px);
  display: flex;
  gap: 48px;
  justify-content: space-between;
`;

const ButtonBox = styled.div`
  margin-top: 16px;
  display: flex;
  gap: 28px;
  justify-content: space-between;
`;

const DataBox = styled.div`
  width: 100%;
`;

const InfoBox = styled.div`
  align-items: center;
  display: flex;
  padding: 16px;
  text-align: left;
  margin-top: 16px;
  background: #f7f0ff;
  border-radius: 16px;
`;

const InfoTitle = styled.div`
  margin-left: 8px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  color: #454648;
  line-height: 24px;
`;

const WeeklyImage = styled.div`
  margin-top: 24px;
  width: 372px;
  height: 200px;
  background: #f0f1f3;
  border-radius: 16px;
  overflow: hidden;
  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`;

const ThumbnailImage = styled.div`
  margin-top: 24px;
  width: 376px;
  height: 202px;
  background: #f0f1f3;
  border-radius: 16px;
`;

const InputBoxWithCount = styled.div`
  position: relative;
`;

const InputCount = styled.div`
  position: absolute;
  right: 16px;
  bottom: 16px;
  font-size: 14px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 400;
  font-size: 16px;
  line-height: 24px;
  text-align: right;
  color: #c1c2c3;
`;

const InputBox = styled.input`
  width: 100%;
  box-sizing: border-box;
  margin-top: 16px;
  border-radius: 16px;
  height: 56px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 24px;
  border: 1px solid #c1c2c3;
  padding: 0 78px 0 16px;
  color: ${(props) => (props.value ? "#000000" : "#797a7b")};
`;

const InputSubBox = styled.textarea`
  width: 100%;
  box-sizing: border-box;
  margin-top: 16px;
  border-radius: 16px;
  height: 148px;
  font-family: "Spoqa Han Sans Neo";
  font-style: normal;
  font-weight: 400;
  font-size: 18px;
  line-height: 24px;
  border: 1px solid #c1c2c3;
  padding: 16px;
  color: ${(props) => (props.value ? "#000000" : "#797a7b")};
  resize: none;
  overflow: auto;
`;

const BodyTitleBold = styled.text`
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
  color: #454648;
`;

interface WeeklyPropType {
  refresh: boolean;
  setRefresh: Dispatch<SetStateAction<boolean>>;
}

export const InnerRegistPage = (props: WeeklyPropType) => {
  const { setRefresh, refresh } = props
  var today = new Date()
  const monthForm = (month: string) => {
    if (month.length === 2) {
      return month
    }
    return "0" + month
  }
  var year = today.getFullYear()
  const yearOptions = [
    { value: (year-1).toString(), label: (year-1).toString()+"년" },
    { value: year.toString(), label: year.toString()+"년" },
    { value: (year+1).toString(), label: (year+1).toString()+"년" }
  ]
  const monthOptions = [
    { value: "01", label: "1월"},
    { value: "02", label: "2월"},
    { value: "03", label: "3월"},
    { value: "04", label: "4월"},
    { value: "05", label: "5월"},
    { value: "06", label: "6월"},
    { value: "07", label: "7월"},
    { value: "08", label: "8월"},
    { value: "09", label: "9월"},
    { value: "10", label: "10월"},
    { value: "11", label: "11월"},
    { value: "12", label: "12월"}
  ]

  const [weeklyTitle, setWeeklyTitle] = useState("");
  const [weeklyImage, setWeeklyImage] = useState("");
  const [weeklyYear, setWeeklyYear] = useState(today.getFullYear().toString());
  const [weeklyMonth, setWeeklyMonth] = useState(monthForm((today.getMonth() + 1).toString()));
  const [weeklyTitleCount, setWeeklyTitleCount] = useState(0);
  const [uploadImage, setUploadImage] = useState<any>([]);
  const imageFile = useRef<HTMLInputElement>(null) 
  const openImageFile = () => {
    if (imageFile.current)
      imageFile.current.click()
  }
  const saveImageFile = (event: any) => {
    if (event.target.files !== null && event.target.files[0] !== undefined) {
      setUploadImage(event.target.files[0])
      setWeeklyImage(URL.createObjectURL(event.target.files[0]))
    }
  }
  const deleteImageFile = () => {
    setWeeklyImage("")
    setUploadImage([false])
  }
  const handleEdit = async () => {
    const response = await createChurchWeekly(uploadImage, weeklyTitle, weeklyYear, weeklyMonth)
    response?.success === true && setRefresh(!refresh)
    response?.success === true && setWeeklyTitle("")
    response?.success === true && setWeeklyImage("")
  };

  return (
    <Container>
      <Header>
        <MainTitleText>주보 등록</MainTitleText>
        <ModifyButtonBox>
          <ModifyButton
            style={{
              background: "#5500DD",
              border: "none",
              color: "#ffffff",
            }}
            onClick={handleEdit}
          >
            등록
          </ModifyButton>
        </ModifyButtonBox>
      </Header>
      <DataBoxContainer>
        <DataContainer><DataBox>
          <SubTitleText>제목</SubTitleText>
          <InputBoxWithCount>
            <InputBox
              type="text"
              maxLength={20}
              value={weeklyTitle}
              placeholder="제목 입력"
              onChange={(e) => {
                setWeeklyTitle(e.target.value);
                setWeeklyTitleCount(e.target.value.length);
              }}
            />
            <InputCount>({weeklyTitleCount}/20)</InputCount>
          </InputBoxWithCount>
        </DataBox>
        </DataContainer>
        <DataContainer>
          <DataBox>
            <SubTitleText>주보 이미지</SubTitleText>
            <InfoBox>
              <img src="/source/icon/information.svg" alt="information" />
              <InfoTitle>주보 이미지를 스캔하거나 촬영하여 등록해주세요.</InfoTitle>
            </InfoBox>
            <Select options={yearOptions} defaultValue={yearOptions[1]} onChange={(e) => e && setWeeklyYear(e?.value)} isSearchable={false} styles={{
              control: (baseStyles, state) => ({
                ...baseStyles,
                borderColor: state.isRtl ? 'grey' : 'red',
              }),
              }}/>
            <Select options={monthOptions} defaultValue={monthOptions[today.getMonth()]} onChange={(e) => e && setWeeklyMonth(e?.value)} isSearchable={false}/>
            <WeeklyImage><img src={weeklyImage ? weeklyImage : "/source/none_image.png"} alt="공지 이미지" /></WeeklyImage>
            <ButtonBox>
              <UploadButton
                style={{
                  background: "#ffffff",
                  border: "1px solid #5500DD",
                  color: "#5500DD",
                }}
                onClick={openImageFile}
                >
                  <input type='file' id='file' ref={imageFile} style={{ display: 'none' }} onChange={saveImageFile} />
                업로드
              </UploadButton>
              <DeleteButton
                style={{
                  background: "#ffffff",
                  border: "1px solid #D40030",
                  color: "#D40030",
                }}
                onClick={deleteImageFile}
              >
                삭제
              </DeleteButton>
            </ButtonBox>
          </DataBox>
        </DataContainer>
        <div
          style={{
            height: "48px",
          }}
        />
      </DataBoxContainer>
      <div
        style={{
          height: "48px",
        }}
      />
    </Container >
  );
};
