import React, { Dispatch, SetStateAction, useState } from 'react'
import {
  FlexRowContainer,
  FlexRowInnerLeft,
  FlexRowInnerRight,
  InnerBoxContainer,
  InnerBoxHeader,
  InnerBoxTitle,
  InputBox,
  InputBoxWithCount,
  InputCount,
  MainContainer,
  Margin16,
  Margin24,
  Margin36,
  SubTitle,
} from '../../../../../component/styledComponent'
import styled from 'styled-components'

const RegisterButton = styled.button`
  color: #ffffff;
  background-color: #d40030;
  width: 120px;
  height: 40px;
  border: none;
  cursor: pointer;
  border-radius: 16px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
`

const Time = styled.div`
  width: calc(50% - 18px);
`
const TimeDivider = styled.div`
  width: 36px;
  margin-top: 56px;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-weight: 700;
  font-size: 16px;
  line-height: 24px;
`
const DaysContainer = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
`
const DayContainer = styled.div`
  display: flex;
  flex-direction: row;
  font-family: 'Spoqa Han Sans Neo';
  font-style: normal;
  font-size: 16px;
  line-height: 24px;
  background: #ffffff;
`
const ClickedDayText = styled.text`
  color: #5500dd;
`
const Check = styled.image`
  width: 24px;
  height: 24px;
  margin-left: 8px;
`

const days = [
  '월요일',
  '화요일',
  '수요일',
  '목요일',
  '금요일',
  '토요일',
  '일요일',
]
interface DayProps {
  weekday: string | undefined
  index: number
  setWeekday: Dispatch<SetStateAction<string>>
}
const Day = (props: DayProps) => {
  const { weekday, index, setWeekday } = props

  return (
    <DayContainer
      onClick={() => {
        setWeekday(index.toString())
      }}
    >
      {weekday === index.toString() ? (
        <ClickedDayText>{days[index]}</ClickedDayText>
      ) : (
        days[index]
      )}

      <Check>
        <img
          src={
            weekday === index.toString()
              ? '/source/icon/check.svg'
              : '/source/icon/dark_check.svg'
          }
          alt=""
        />
      </Check>
    </DayContainer>
  )
}

export const InnerScheduleRegist = () => {
  const [title, setTitle] = useState<string>()
  const [subtitle, setSubtitle] = useState<string>()
  const [weekday, setWeekday] = useState<string>()
  const [place, setPlace] = useState<string>('')
  const [mc, setMc] = useState<string>('')
  const [target, setTarget] = useState<string>('')
  const [reference, setReference] = useState<string>('')
  const [startTime, setStartTime] = useState<string>()
  const [endTime, setEndTime] = useState<string>()
  return (
    <MainContainer>
      <InnerBoxHeader>
        <InnerBoxTitle>예배 일정 등록</InnerBoxTitle>
        <RegisterButton onClick={() => setShowModal(true)}>
          내보내기
        </RegisterButton>
      </InnerBoxHeader>
      <InnerBoxContainer>
        <Margin36 />
        <FlexRowContainer>
          <FlexRowInnerLeft>
            <SubTitle>예배명</SubTitle>
            <InputBox
              type="text"
              maxLength={20}
              value={title}
              placeholder="예배명 입력"
              onChange={e => setTitle(e.target.value)}
            />
          </FlexRowInnerLeft>
          <FlexRowInnerRight>
            <SubTitle>소제목</SubTitle>
            <InputBox
              type="text"
              maxLength={20}
              value={subtitle}
              placeholder="소제목 입력"
              onChange={e => setSubtitle(e.target.value)}
            />
          </FlexRowInnerRight>
        </FlexRowContainer>
        <Margin36 />
        <FlexRowContainer>
          <Time>
            <SubTitle>시간</SubTitle>
            <InputBox
              type="text"
              maxLength={20}
              value={startTime}
              placeholder="00.01.01"
              onChange={e => setStartTime(e.target.value)}
            />
          </Time>
          <TimeDivider>~</TimeDivider>
          <Time>
            <Margin24 />
            <InputBox
              type="text"
              maxLength={20}
              value={endTime}
              placeholder="00.01.01"
              onChange={e => setEndTime(e.target.value)}
            />
          </Time>
        </FlexRowContainer>
        <Margin36 />
        <SubTitle>요일</SubTitle>
        <Margin16 />
        <DaysContainer>
          {days.map((day, index) => {
            return (
              <Day
                setWeekday={() => setWeekday(index.toString())}
                weekday={weekday}
                index={index}
              />
            )
          })}
        </DaysContainer>
        <Margin36 />
        <FlexRowContainer>
          <FlexRowInnerLeft>
            <SubTitle>장소</SubTitle>
            <InputBoxWithCount>
              <InputBox
                type="text"
                maxLength={15}
                value={place}
                placeholder="장소 입력"
                onChange={e => setPlace(e.target.value)}
              />
              <InputCount>({place.length}/15)</InputCount>
            </InputBoxWithCount>
            <Margin36 />
            <SubTitle>대상</SubTitle>
            <InputBoxWithCount>
              <InputBox
                type="text"
                maxLength={15}
                value={target}
                placeholder="대상 입력"
                onChange={e => setTarget(e.target.value)}
              />
              <InputCount>({target.length}/15)</InputCount>
            </InputBoxWithCount>
          </FlexRowInnerLeft>
          <FlexRowInnerRight>
            <SubTitle>설교/진행</SubTitle>
            <InputBoxWithCount>
              <InputBox
                type="text"
                maxLength={15}
                value={mc}
                placeholder="설교/진행 입력"
                onChange={e => setMc(e.target.value)}
              />
              <InputCount>({mc.length}/15)</InputCount>
            </InputBoxWithCount>
            <Margin36 />
            <SubTitle>참고</SubTitle>
            <InputBoxWithCount>
              <InputBox
                type="text"
                maxLength={15}
                value={reference}
                placeholder="참고 입력"
                onChange={e => setReference(e.target.value)}
              />
              <InputCount>({reference.length}/15)</InputCount>
            </InputBoxWithCount>
          </FlexRowInnerRight>
        </FlexRowContainer>
        <Margin36 />
      </InnerBoxContainer>
      <Margin24 />
    </MainContainer>
  )
}
